import { FastField } from "formik";
import React from "react";
import { Spinner } from "react-bootstrap";

export const SubmitBtn = ({ categoryId }) => {
  return (
    <FastField>
      {({ form }) => {
        return (
          <>
            <button type="btn" className="btn" disabled={form.isSubmitting}>
              {categoryId ? (
                <>
                <span className="btn btn-warning"> ذخیره تغییرات </span>
               
                {form.isSubmitting ? (
                <Spinner
                  className="me-3 text-dark"
                  animation="border"
                  variant="light"
                  size="sm"
                />
              ) : null}
                </>
              ) : (
                <>
                <span className="btn btn-success">ذخیره </span>
                </>
                
              )}
             
            </button>
          </>
        );
      }}
    </FastField>
  );
};
