import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { AddItem_btn } from "../../general_compo/add_or_edit_product/AddItem_btn";
import { Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import { initialValues, onSubmit } from "./FormikHelper_roles";
import { SubmitBtn } from "../../formik/SubmitBtn";
import { useEffect } from "react";

export const AddNewRole_modal = ({ rowDataToEdit }) => {
  const [reInitialize, setReInitialize] = useState({});
  useEffect(() => {
    console.log(rowDataToEdit);
    setReInitialize(reInitialize);
   
  }, []);



  
  const handleResetForm = () => {
    document.getElementById("formOfRoles").reset();
    console.log("test");
  };

  return (
    <div>
      {/* <!----- Button trigger modal ➕ icon -----> */}
      <span data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        {/* <NavLink to={"/roles/add-edit-role"}> */}
        <AddItem_btn />
        {/* </NavLink> */}
      </span>

      {/* <!----------------- Modal ---------------> */}
      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content animate__animated animate__pulse">
            <div className="modal-header d-flex justify-content-between align-items-center">
              <h5 className="modal-title w-100 mx-5" id="staticBackdropLabel">
                افزودن کد تخفیف
              </h5>
              <NavLink to={"/roles"} className="ps-4">
                {/* <!------------ ❌ close btn ------------> */}
                <button
                  id="btn-close-modal-roles"
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                  onClick={() => handleResetForm()}
                ></button>
              </NavLink>
            </div>

            <div className="modal-body">
              <Formik
                initialValues={reInitialize || initialValues}
                onSubmit={(values, action) =>
                  onSubmit(values, action, rowDataToEdit)
                }
                enableReinitialize
              >
                {(form) => {
                  // console.log(form.values);
                  return (
                    <Form id="formOfRoles">
                      <FormikControl
                        control="input"
                        name="title"
                        title="عنوان نقش"
                        placeholder="فقط حروف فارسی و لاتین"
                      />
                      <FormikControl
                        control="textArea"
                        name="description"
                        title="توضیحات نقش"
                        placeholder="فقط حروف فارسی و لاتین"
                      />
                      <SubmitBtn />
                    </Form>
                  );
                }}
              </Formik>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
