import jMoment from "jalali-moment";

export const ConvertPersianDateToMiladi = (date) => {
  console.log(date);
  const persianDateWithoutHyphen = date.replace(/-/g, ""); // حذف خط تیره‌ها

  const persianDate = jMoment(persianDateWithoutHyphen, "jYYYYjMMjDD").format(
    "YYYY/MM/DD"
  );

  const miladiDate = jMoment(persianDate).locale("en").format("YYYY-MM-DD");
 
  return miladiDate;
};
