import { Field, Form, Formik } from "formik";
import React from "react";
import { Chips } from "../../general_compo/spinners&chips/Chips";
import { handleOnChange } from "../../pages/2-product/add_or_edit_product/Get_Items_dropdowns";
import { logDOM } from "@testing-library/react";

export const MultiSelect_Input = (props) => {
  const { chips, setChips, form, label, option, name, stateOfData } = props;
  return (
    <div>
      <div className="p-2">
        <div className="input-group mb-1">
          <span className="input-group-text w_6rem customStyle">{label}</span>
          <Formik>
            {form=>{

            <Field
              as="select"
              className="form-control form-select"
              name={name}
              onChange={(e) => {
                console.log(option);
                handleOnChange(e, form, option, setChips);
              }}
            >
              <option hidden value>
                --
              </option>

              {option?.map((d) => (
                <option value={d.id} key={Math.random()}>
                  {d.value}
                </option>
              ))}
            </Field>
            }}
            
          </Formik>
        </div>
      </div>
      <Chips chips={chips} setChips={setChips} />
    </div>
  );
};
