import React from "react";
import { useState } from "react";
import { getProductsService } from "../../../../services/CRUD_categoryService";
import { useEffect } from "react";
import { Operation_products } from "../../../pages/2-product/table_addition/Operation_products";
import { UpperPartPages } from "../../reusable_operations/UpperPartPages";

export const Reusable_table2 = () => {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1); //صفحه فعلی
  const [countOfFetchProducts, setCountOfFetchProducts] = useState(5); //تعداد محصولاتی که در هر صفحه نمایش داده شود
  const [countOfPages, setCountOfPages] = useState(1); //تعداد کل صفحات
  const [searchChar, setSearchChar] = useState("");

  //============ هدرهای جدول📍==================
  const headers_productTable = [
    { field: "id", title: "#" },
    {
      field: null,
      title: "گروه محصول",
      elements: (rowData) => {
        return rowData.categories[0] ? (
          `${rowData.categories[0]?.title}`
        ) : (
          <span className="text-danger">ندارد</span>
        );
      },
    },
    { field: "title", title: "عنوان" },
    { field: "price", title: "قیمت" },
    { field: "stock", title: "موجودی" },
    {
      field: null,
      title: "عملیات",
      elements: (rowData) => <Operation_products rowData={rowData} />,
    },
  ];



  //============ 📍 پارامترهای سرچ باکس ====================
  const searchParams = {
    title: "جستجو",
    placeholder: "قسمتی از عنوان را وارد کنید",
  };




  //============= 📍 گ
  const handleGetProducts = async (page, count, searchChar) => {
    const res = await getProductsService(page, count, searchChar);
    console.log(res);
    if (res.status == 200) {
      setData(res.data.data);
      setCountOfPages(res.data.last_page);
    }
  };
  const handleSearch = (char) => {
    setSearchChar(char);
    handleGetProducts(1, countOfFetchProducts, char);
  };

  useEffect(() => {
    handleGetProducts(currentPage, countOfFetchProducts, searchChar);
  }, []);
  //========================================
  const pageRange = 3;

  const [pages, setPages] = useState([]);

  let timeout;

  const handleSetSearchChar = (char) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => {
      handleSearch(char);
    }, 2000);
  };

  useEffect(() => {
    let pArr = [];
    for (let i = 1; i <= countOfPages; i++) pArr.push(i);
    setPages(pArr);
  }, [countOfPages]);

  return (
    <div className="p-4 reusable_table">
      <UpperPartPages title="مدیریت محصولات" />
      {/* ================== 🔍 start searchBox👇 ================== */}
      <div className="input-group mb-3 searchBox">
        <span className="input-group-text searchButton " id="basic-addon2">
          {searchParams.title}
        </span>
        <input
          type="text"
          className="form-control searchInput"
          placeholder={searchParams.placeholder}
          onChange={(e) => handleSetSearchChar(e.target.value)}
        />
      </div>

      <table className=" table_of_reusable_table table table-striped table-responsive text-center table-hover table-bordered">
        <thead>
          <tr>
            {headers_productTable.map((header) => (
              <th>{header.title}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((d) => (
            <tr>
              {headers_productTable.map(
                (header) =>
                  header.field ? (
                    <td>{d[header.field]}</td>
                  ) : (
                    <td>{header.elements(d)}</td>
                  )
                // <td>{header.elements(d)}</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
      {pages.length > 1 ? (
        <nav
          aria-label="Page navigation example"
          className="d-flex justify-content-center"
        >
          <ul className="pagination dir_ltr">
            <li className="page-item">
              <span
                className={`page-link pointer ${
                  currentPage == 1 ? "disabled" : ""
                }`}
                aria-label="Previous"
                onClick={() => setCurrentPage(currentPage - 1)}
              >
                <span aria-hidden="true">&raquo;</span>
              </span>
            </li>

            {currentPage > pageRange ? (
              <li className="page-item me-2">
                <span
                  className="page-link pointer"
                  onClick={() => setCurrentPage(1)}
                >
                  1
                </span>
              </li>
            ) : null}

            {pages.map((page) => {
              return page < currentPage + pageRange &&
                page > currentPage - pageRange ? (
                <li className="page-item" key={page}>
                  <span
                    className={`page-link pointer ${
                      currentPage == page ? "alert-success" : ""
                    }`}
                    onClick={() => setCurrentPage(page)}
                  >
                    {page}
                  </span>
                </li>
              ) : null;
            })}

            {currentPage <= countOfPages - pageRange ? (
              <li className="page-item ms-2">
                <span
                  className="page-link pointer"
                  onClick={() => setCurrentPage(countOfPages)}
                >
                  {countOfPages}
                </span>
              </li>
            ) : null}

            <li className="page-item">
              <span
                className={`page-link pointer ${
                  currentPage == countOfPages ? "disabled" : ""
                }`}
                aria-label="Next"
                onClick={() => setCurrentPage(currentPage + 1)}
              >
                <span aria-hidden="true">&laquo;</span>
              </span>
            </li>
          </ul>
        </nav>
      ) : null}
    </div>
  );
};
