import * as Yup from "yup";
import {
  createNewGuaranteeService,
  updateGuaranteeService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  descriptions: "",
  length: "",
  length_unit: `e`,
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  setData,
  guaranteeToEdit,
  setGuaranteeToEdit
) => {

  if (guaranteeToEdit?.length) {
    const res = await updateGuaranteeService(values, guaranteeToEdit.id);
    if (res.status == 200) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");

      setData((oldData) => {
        let newData = [...oldData];
        let index = oldData.findIndex((d) => d.id == guaranteeToEdit.id);
        newData[index] = res.data.data;
        return newData;
      });
      setGuaranteeToEdit(res.data.data);
    }
  } else {
    const newValues ={
      title: values,
      descriptions: "",
      length: "",
      length_unit: `e`,
    }
    const res = await createNewGuaranteeService(values);
    console.log(values);
    if (res.status == 201) {
      setData((data) => [...data, res.data.data]);
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");
      actions.resetForm();
    }
  }
};
//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
