import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_brands";

export const AddNewBrands = () => {
  return (
    <div className="container">
      <div>
        <div className="d-flex justify-content-between pt-3">
          <div className="fs-4">افزودن برند جدید</div>
          <PrevPageBtn
            customStyle="fs-2 text-start text-secondary"
            returnTitle={<i className="fa-solid fa-xmark"></i>}
          />
        </div>
        <hr />
      </div>
      <div>
        <Formik
          initialValues={initialValues}
          onSubmit={onSubmit}
          validationSchema={validationSchema}
        >
          {(formik) => {
            console.log(formik);
            return (
              <Form>
                <div className="row">
                  <div className="col-lg-6">
                    <FormikControl
                      control="input"
                      type="text"
                      name="original_name"
                      placeholder="عنوان لاتین"
                    />
                  </div>
                  <div>

                  <FormikControl
                    control="input"
                    type="text"
                    name="persian_name"
                    placeholder="عنوان فارسی"
                  />
                  </div>
                  <FormikControl
                    control="textArea"
                    type="text"
                    name="descriptions"
                    placeholder="توضیحات"
                  />
                  <FormikControl
                    formik={formik}
                    control="addFile"
                    type="text"
                    label="تصویر"
                    name="logo"
                    placeholder="برای افزودن تصویر اینجا کلیک کنید"
                  />
                  <div className="text-center">
                    <SubmitBtn />
                  </div>
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
