import { FastField } from "formik";
import React, { useState } from "react";
import { useEffect } from "react";

export const SwitchCheckBox = ({ label, name, getAttToEdit }) => {
  const [changeRadio, setChangeRadio] = useState(
    getAttToEdit?.switchShowFilter == 0 ? false : true
  );
  useEffect(() => {
    console.log(getAttToEdit?.switchShowFilter);
  }, [getAttToEdit]);

  return (
    <div className="form-check form-switch pt-4">
      <FastField
        id={name}
        name={name}
        type="checkbox"
        className="form-check-input pointer"
      >
        {() => (
          <>
            <input
              id={name}
              type="checkbox"
              className="form-check-input pointer"
              value={changeRadio}
              onChange={() => {
                setChangeRadio(!changeRadio);
              }}
            />
            <label className="form-check-label" htmlFor={name}>
              {label}
            </label>
          </>
        )}
      </FastField>
    </div>
  );
};

