import axios from "axios";
import { json } from "react-router-dom";
import swal from "sweetalert";
import * as Yup from "yup";

// ==============  üìçinitialValues login ===================
export const initialValues = {
  phone: "",
  password: "",
  remember: false,
};


//===============  üìçvalidationSchema login ===============
export const validationSchema = Yup.object({
  phone: Yup.number()
    .typeError("ŸÇÿßŸÑÿ® ÿ¥ŸÖÿßÿ±Ÿá ÿ™ŸÑŸÅŸÜ ÿ±ÿß ÿ±ÿπÿß€åÿ™ ⁄©ŸÜ€åÿØ")
    .required("ŸÅ€åŸÑÿØ ÿßÿ¨ÿ®ÿßÿ±€å"),

  password: Yup.string().required("ŸÅ€åŸÑÿØ ÿßÿ¨ÿ®ÿßÿ±€å"),

});


//================  üìçonSubmit login ======================
export const onSubmit = (values, submitMethods, navigate) => {


  axios
    .post("http://ecomadminapi.azhadev.ir/api/auth/login", {
      ...values,
      remember: values.remember ? 1 : 0,
    })
    .then((res) => {
      if (res.status == 200) {
        //ÿß⁄Øÿ± ŸÑÿß⁄Ø€åŸÜ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿßŸÜÿ¨ÿßŸÖ ÿ¥ÿØ
        // ÿ™Ÿà⁄©ŸÜ ÿßÿ¥ ÿ±Ÿà ÿØÿ± ŸÑŸà⁄©ÿßŸÑ ÿßÿ≥ÿ™Ÿàÿ±€åÿ¨ ÿ∞ÿÆ€åÿ±Ÿá ⁄©ŸÜ
        //Ÿà ⁄©ÿßÿ±ÿ®ÿ± ÿ±Ÿà ÿ®ŸÅÿ±ÿ≥ÿ™ ÿ®Ÿá ÿµŸÅÿ≠Ÿá ÿßÿµŸÑ€å
        //useNavigate ÿ®ÿß ⁄©ŸÖ⁄©
        localStorage.setItem("loginToken", JSON.stringify(res.data));
        navigate("/");

        // alert("Ÿàÿ±ŸàÿØ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿßŸÜÿ¨ÿßŸÖ ÿ¥ÿØ");
      } else if (res.status != 200) {
        const msg = res.data.message;
        swal("ŸÖÿ™ÿßÿ≥ŸÅŸÖ!", msg, "error");
      }
      console.log(res);
    })
    .catch((error) => {
      const msg = error.message;
      swal(
        "ŸÖÿ¥⁄©ŸÑ€å ÿßÿ≤ ÿ≥ŸÖÿ™ ÿ≥ÿ±Ÿàÿ± ÿ±ÿÆ ÿØÿßÿØŸá!",
        "ŸÑÿ∑ŸÅÿß ÿ®ÿπÿØÿß ŸÖÿ¨ÿØÿØÿß ÿ™ŸÑÿßÿ¥ ⁄©ŸÜ€åÿØ...",
        "error"
      );
      console.error("error :" + error.response.data);
    });
};
