import { FastField } from "formik";
import React from "react";
import { useEffect } from "react";

export const Radiobtn = ({ lengthUnit, setLengthUnit }) => {
  useEffect(() => {
    console.log(lengthUnit);
  }, [lengthUnit]);
  return (
    <div>
      <FastField>
        {({ form }) => {
          console.log(form.values.length_unit);
          return (
            <>
              <div class="control d-flex align-items-center">
                <span>بر حسب : </span>
                <label class="radio ps-1">
                  سال
                  <input
                    type="radio"
                    name="answer"
                    value="سال"
                    onClick={(e) => {
                      setLengthUnit(e.target.value);
                    }}
                  />
                </label>
                <label class="radio">
                  ماه
                  <input
                    type="radio"
                    name="answer"
                    value="ماه"
                    onClick={(e) => {
                      setLengthUnit(e.target.value);
                      console.log(form.values.length_unit);
                    }}
                    defaultChecked={
                      form.values.length_unit == "ماه" ? true : false
                    }

                    //defaultChecked={true}
                  />
                </label>
              </div>
            </>
          );
        }}
      </FastField>
    </div>
  );
};
