import React, { useContext, useEffect } from "react";
import { NavLink, useLocation } from "react-router-dom";
import { FormikAddItems } from "../formik/FormikAddItems";
import { CategoryContext } from "../../context/CategoryContext";

export const AddCompo = (props) => {
  const location = useLocation();
  const { title, exit_icon, hidden_compo } = props;
  const { editId, setEditId } = useContext(CategoryContext);

  console.log(editId);


  // console.log(props);
  //console.log(location);
  // console.log(location.state?.categoryId);
  // console.log(location.state?.categoryTitle);
  // console.log(location.state?.categoryDescriptions);

  const handleHideCompo = () => {
    document
      .getElementById("compo_for_add_items")
      .classList.remove("show_screen");

    document
      .getElementById("compo_for_add_items")
      .classList.add("hidden_screen");
  };

  return (
    <div
      className={`addProduct position-relative ${hidden_compo ? "d-none" : ""}`}
      id="compo_for_add_items"
    >
      <div className="text-center">
        شناسه محصول: {location.state?.categoryId}
      </div>
      <div className="header_addProduct p-3 d-flex justify-content-between">
        <h5>{title}</h5>

        {/* ================  in icon ❌  ================ */}
        <NavLink to={exit_icon}>
          <button
            type="button"
            className="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
            onClick={() => (hidden_compo ? handleHideCompo() : null)}
          ></button>
        </NavLink>
      </div>
      {/* ================  formik inputs  ================ */}
      <div className=" d-flex p-3 justify-content-center row">
        <div className=" col-lg-5">
          <FormikAddItems
            editId={editId}
            setEditId={setEditId}
            parent_title={location.state?.myState}
            categoryTitle={location.state?.categoryTitle}
            categoryDescriptions={location.state?.categoryDescriptions}
          />
        </div>
      </div>
    </div>
  );
};
