import { ErrorMessage, FastField, Form, Formik } from "formik";
import React from "react";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_colour";
import { useEffect } from "react";
import { useState } from "react";

export const AddNewColor = ({ colorToEdit, data, setData }) => {
  const [reInitialize, setReInitialize] = useState([]);

useEffect(()=>{
  setReInitialize({
    title:``
  })
},[])


  //==== ÿ®Ÿá ÿµŸàÿ±ÿ™ ÿØÿ≥ÿ™€å ÿ®ÿß€åÿØ ÿ±ŸÜ⁄Ø Ÿáÿß ÿ±Ÿà ÿ™ÿ∫€å€åÿ± ÿ®ÿØ€åŸÖ =============
  const handleChangeColorCodeField = (e, form) => {
    console.log(e);
    form.setFieldValue("code", e.target.value);
    console.log(e.target.value);
  };

  //==================== üìçreturn ==============================
  return (
    <div className="modal-content">
      <Formik
        initialValues={colorToEdit || initialValues}
        onSubmit={(values, actions) => onSubmit(values, actions, setData)}
        validationSchema={validationSchema}
        enableReinitialize
      >
        {(formik) => {
          //console.log(formik.values);
          return (
            <Form>
              {/* ==============  modal-header ============= */}
              <div className="modal-header">
                <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                  ÿßÿ∂ÿßŸÅŸá ⁄©ÿ±ÿØŸÜ ⁄Øÿßÿ±ÿßŸÜÿ™€å ÿ¨ÿØ€åÿØ
                </h1>
              </div>
              {/* ==============  modal-body ============== */}
              <FormikControl
                control="input"
                type="text"
                name="title"
                placeholder="ÿ±ŸÜ⁄Ø"
              />
              <div>
                <FastField>
                  {({ form }) => {
                    //console.log(form);
                    return (
                      <div className="d-flex align-items-center justify-content-start m-3">
                        <label htmlFor="code_color" className="ps-3">
                          ⁄©ÿØ ÿ±ŸÜ⁄Ø
                        </label>
                        <input
                          type="color"
                          id="code_color"
                          onChange={(e) => handleChangeColorCodeField(e, form)}
                        />
                      </div>
                    );
                  }}
                </FastField>
                <ErrorMessage
                  name="code"
                  render={(msg) => <small className="text-danger">{msg}</small>}
                />
              </div>
              <div className="text-center  pb-3">
                <SubmitBtn />
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};
