export const initialValues = {
  title: "",
  code: "#e66465",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (values, actions, setData) => {
  console.log(values);
  const res = await createNewColorService(values);
  if (res.status == 201) {
    swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
    setData((oldData)=>[...oldData ,])
  }
};

