import React, { useEffect } from "react";
import { UpperPartPages } from "../../general_compo/reusable_operations/UpperPartPages";
import { getBrandsService } from "../../../services/CRUD_categoryService";
import { useState } from "react";
import { Reusable_table } from "../../general_compo/reusable_table/Reusable_table";
import { apiPath } from "../../../services/httpService";
import { Operations_brands } from "./Operations_brands";
import { Modal } from "react-bootstrap";
import { AddNewBrands } from "./add_or_edir_brands/AddNewBrands";
import { Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_brands";
import { SubmitBtn } from "../../formik/SubmitBtn";

export const BrandsManagement = () => {
  const [data, setData] = useState([]);

  //================== 📍هدرهای جدول  ================
  const header_brandsTable = [
    { field: "id", title: "id" },
    { field: "original_name", title: "عنوان لاتین" },
    { field: "persian_name", title: "عنوان فارسی" },
    { field: "descriptions", title: "توضیحات" },
  ];

  //=============  📍Get all brands =====================
  const handleGetBrands = async () => {
    const res = await getBrandsService();
    try {
      if (res.status == 200) {
        setData(res.data.data);
      }
    } catch {}
  };
  useEffect(() => {
    handleGetBrands();
  }, []);

  //=============  📍additionField =====================
  const additionField = [
    {
      title: "لوگو",
      elements: (rowData) => (
        <>
          {rowData.logo ? (
            <img
              src={apiPath + rowData.logo}
              alt={`${rowData.original_name} logo`}
              width="40"
            />
          ) : (
            "null"
          )}
        </>
      ),
    },

    {
      title: "عملیات",
      elements: (rowData) => (
        <>
          <Operations_brands rowData={rowData} data={data} setData={setData} />
        </>
      ),
    },
  ];

  //======================  return  =======================
  return (
    <>
      <div className="px-4 productGroupManagement">
        
        {/* <UpperPartPages title="مدیریت برندها" />
        <Reusable_table
          nameOfColumn={header_brandsTable}
          dataOfRows={data}
          placeholder_searchBox="قسمتی از عنوان را وارد کنید"
          go_where='/adding-brands'
          show_subGroup={true}
          show_addButton={true}
          having_searchBox={true}
          additionField={additionField}
          show_subset_icon={true}
          titleName="original_name"
        /> */}

        <div className="modal_compo">
          <button
            type="button"
            className="btn btn-success d-flex justify-content-center align-items-center"
            data-bs-toggle="modal"
            data-bs-target="#staticBackdrop"
          >
            <i className="fas fa-plus text-light"></i>
          </button>

       
     <UpperPartPages title="مدیریت برندها" />
        <Reusable_table
          nameOfColumn={header_brandsTable}
          dataOfRows={data}
          placeholder_searchBox="قسمتی از عنوان را وارد کنید"
          go_where='/adding-brands'
          show_subGroup={true}
          show_addButton={true}
          having_searchBox={true}
          additionField={additionField}
          show_subset_icon={true}
          titleName="original_name"
        /> 
          {/* ================= 📍start modal ================= */}
          <div
            className="modal fade modal-dialog modal-dialog-centered"
            id="staticBackdrop"
            data-bs-backdrop="static"
            data-bs-keyboard="false"
            tabindex="-1"
            aria-labelledby="staticBackdropLabel"
            aria-hidden="true"
          >
          <div>testtttt</div>
            <div className="modal-dialog">
              <div className="modal-content">
                {/* ==============  modal-header ============= */}
                <div className="modal-header">
                  <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                    افزودن برند جدید
                  </h1>
                  <button
                    type="button"
                    className="btn-close "
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  ></button>
                </div>
                {/* ==============  modal-body ============== */}
                <div className="modal-body ">
                  <Formik
                    initialValues={initialValues}
                    onSubmit={onSubmit}
                    validationSchema={validationSchema}
                  >
                    {(formik) => {
                      console.log(formik);
                      return (
                        <Form>
                          <FormikControl
                            control="input"
                            type="text"
                            name="original_name"
                            placeholder="عنوان لاتین"
                          />

                          <FormikControl
                            control="input"
                            type="text"
                            name="persian_name"
                            placeholder="عنوان فارسی"
                          />

                          <FormikControl
                            control="textArea"
                            type="text"
                            name="descriptions"
                            placeholder="توضیحات"
                          />

                          <FormikControl
                            formik={formik}
                            control="addFile"
                            type="text"
                            label="تصویر"
                            name="logo"
                            placeholder="برای افزودن تصویر اینجا کلیک کنید"
                          />
                          <div className="text-center">
                            <SubmitBtn />
                          </div>
                        </Form>
                      );
                    }}
                  </Formik>
                </div>
              </div>
            </div>
          </div>
          {/* ================= 📍end modal ================= */}
        </div>
      </div>
    </>
  );
};
