import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  validationSchema,
} from "../../5-colour/FormikHelper_colour";
import { onSubmit } from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import { getCategoriesService } from "../../../../services/CRUD_categoryService";
import { useLocation } from "react-router-dom";

export const AddProduct = () => {
  const [mainCategories, setMainCategories] = useState([]);
  const location = useLocation();
  console.log(location);
  const handleGetCategories = async () => {
    const res = await getCategoriesService();
    if (res.status == 200) {
      //console.log(res.data.data);
      setMainCategories(res.data.data);
    }
  };

  useEffect(() => {
    handleGetCategories();
  }, []);

  return (
    <div className="">
      {/* ================= üìç header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h3>title_of_page</h3>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== üìç start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={initialValues}
          onSubmit={onSubmit}
          validationSchema={validationSchema}
        >
          {(form) => {
            return (
              <Form>
                {/* {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label=""
                    option={mainCategories}
                    name=""
                    parent_title=""
                  />
                ) : (
                  "ÿØÿ≥ÿ™Ÿá ŸàÿßŸÑÿØ"
                )} */}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="ÿπŸÜŸàÿßŸÜ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  placeholder="ŸÇ€åŸÖÿ™"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  placeholder="Ÿàÿ≤ŸÜ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="color_ids"
                  placeholder="ÿ±ŸÜ⁄Ø"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="guarantee_ids"
                  placeholder="⁄Øÿßÿ±ÿßŸÜÿ™€å"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" ÿ™⁄Ø Ÿáÿß"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  placeholder="ŸÖŸàÿ¨ŸàÿØ€å"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  placeholder="ÿ™ÿÆŸÅ€åŸÅ"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
