import React from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { getAttributesService } from "../../../../services/CRUD_categoryService";
import { useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_AddAtt_products";
import { SubmitBtn } from "../../../formik/SubmitBtn";

export const AddAttributes = () => {
  const location = useLocation();
  const [typeAtt, setTypeAtt] = useState();
  const [Id_product, setId_product] = useState([]);

  useEffect(() => {
    setId_product(location.state.rowData.id);
  }, [location]);

  //============= ⁄Øÿ±ŸÅÿ™ŸÜ ÿ™ÿß€åŸæ Ÿà€å⁄ò⁄Ø€å Ÿáÿß ÿßÿ≤ ŸÖÿ≠ÿµŸàŸÑ ŸàÿßŸÑÿØ =============

  const handleGetTypeOfAtt = async () => {
    Promise.all(
      location.state.rowData.categories.map(async (cat) => {
        const res = await getAttributesService(cat.id);
        if (res.status == 200) {
          setTypeAtt((oldAttr) => {
            return oldAttr
              ? [...oldAttr, { groupTitle: cat.title, data: res.data.data }]
              : [{ groupTitle: cat.title, data: res.data.data }];
          });
        }
      })
    );
  };
  useEffect(() => {
    handleGetTypeOfAtt();
  }, []);

  useEffect(() => {
    console.log(typeAtt.map(attr => attr.data.Arr));
  }, [typeAtt]);

  //======================== üìç return =============================
  return (
    <div className=" container mt-5">
      {/* ================= üìç üëáheader of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container ">
        <span className="d-flex">
          <h5 className="fw-bold">ÿßŸÅÿ≤ŸàÿØŸÜ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ :</h5>
          <h5 className="text-primary px-2">{location.state.rowData.title}</h5>
        </span>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>
      {/* ================= üìç üëÜheader of page  ==================== */}
      <div className="d-flex justify-content-center">
        <div className="col-12 col-xxl-7 col-xl-8 col-lg-10">
          <Formik
            initialValues={initialValues}
            onSubmit={(value) => onSubmit(Id_product, value)}
            validationSchema={validationSchema}
          >
            {(formik) => {
              return (
                <div className="">
                  {/* {typeAtt?.map(i => i.data) =} */}
                  <Form>
                    {typeAtt?.map((item, index) => (
                      <div className="py-2">
                        <div className="text-center py-2">
                          <span className="fs-4"> ⁄Øÿ±ŸàŸá:</span>
                          <span className="fs-5">{item.groupTitle} </span>
                        </div>
                        {item.data.map((d) => (
                          <FormikControl
                            control="typeOfAtt"
                            titleOfAttr={d.title}
                            unitOfAttr={d.unit}
                          />
                        ))}
                      </div>
                    ))}

                    <div className=" text-center pt-4">
                      <SubmitBtn />
                    </div>
                  </Form>
                </div>
              );
            }}
          </Formik>
        </div>
      </div>
    </div>
  );
};

