import * as Yup from "yup";
import {
  createNewColorService,
  updateColorService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  code: "#fffff",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  colorToEdit,
  setData,
  setColorPickerValue
) => {
  if (colorToEdit) {
    const res = await updateColorService(colorToEdit, colorToEdit.id);
    console.log(res);
    if (res.status == 200) {
      swal("Ÿà€åÿ±ÿß€åÿ¥ ÿ¥ÿØ!...", res.data.message, "success");
      setData((oldData) =>{
        let newData =[...oldData]
        let index= newData.findIndex(d=> d.id == res.data.data.id)
      })
    }
  } else {
    const res = await createNewColorService(values);
    if (res.status == 201) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
      setData((oldData) => [...oldData, res.data.data]);
      setColorPickerValue("#000");
      actions.resetForm();
    }
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
