import axios from "axios";
import { useEffect, useState } from "react";
import { getUserService } from "../../services/authServices";
import { httpService } from "../../services/httpService";

export const useIsLogin = () => {
  const [loading, setLoading] = useState(true);
  const [isLogin, setIsLogin] = useState(false);


  const isLoginService = async () => {
    const loginToken = JSON.parse(localStorage.getItem("loginToken"));
    if (loginToken) {
      const res = await httpService("auth/user", "get", {
        headers: {
          Authorization: `Bearer ${loginToken.token}`,
        },
      });
      try {
        console.log("try");
        setIsLogin(res.status == 200 ? true : false);
        setLoading(false);
      } catch (error) {
        console.log("catch");
        localStorage.removeItem("loginToken");
        setIsLogin(false);
        setLoading(false);
      }
    } else {
      setIsLogin(false);
      setLoading(false);
    }
  };

  console.log("out of use effect");
  useEffect(() => {
    isLoginService();
  }, []);
  // useEffect(() => {
  //   const loginToken = JSON.parse(localStorage.getItem("loginToken"));
  //   if (loginToken) {
  //     const res = getUserService(loginToken);
  //     try {
  //       setIsLogin(res.status == 200 ? true : false);
  //       setLoading(false);
  //     } catch (error) {
  //       localStorage.removeItem("loginToken");
  //       setIsLogin(false);
  //       setLoading(false);
  //     }
  //   } else {
  //     setIsLogin(false);
  //     setLoading(false);
  //   }
  // }, []);

  return [isLogin, loading];
};
