import axios from "axios";
import swal from "sweetalert";
import * as Yup from "yup";

// ==================  ðŸ“initialValues ======================
export const initialValues = {
  phone: "",
  password: "",
  remember: false,
};

//===================  ðŸ“validationSchema ==================
export const validationSchema = Yup.object({
  phone: Yup.number()
    .typeError("Ù‚Ø§Ù„Ø¨ Ø´Ù…Ø§Ø±Ù‡ ØªÙ„ÙÙ† Ø±Ø§ Ø±Ø¹Ø§ÛŒØª Ú©Ù†ÛŒØ¯")
    .required("ÙÛŒÙ„Ø¯ Ø§Ø¬Ø¨Ø§Ø±ÛŒ"),

  password: Yup.string()
    .required("ÙÛŒÙ„Ø¯ Ø§Ø¬Ø¨Ø§Ø±ÛŒ")
    .matches(
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{4,})/,
      "Ù…Ø§Ù†Ù†Ø¯ Ù†Ù…ÙˆÙ†Ù‡: XPp1@c5"
    ),

  c_password: Yup.string()
    .oneOf([Yup.ref("password"), null], "Passwords must match")
    .required("It is required"),
});

//=========================  ðŸ“onSubmit ======================
export const onSubmit = (values) => {
  //   const result = { submit: "Done", value: { values } };
  console.log("test");

  axios
    .post("http://127.0.0.1:8000/api/documentation/api/auth/login", values)
    .then((res) => {
      console.log(res);
      console.log(values);
      localStorage.setItem("savedData", JSON.stringify(values));
      if (res.status == 200) {
        localStorage.setItem("token", res.data.token);
        console.log("Registration was successful");
        swal("", "Your registration was successful!", "success");
      } else if (res.status == 202) {
        swal("This user exists!");
        console.log("This user exists");
      }
    })
    .catch((err) => {
      console.log(err);
      swal("Oops...", "Something went wrong!", "error");
    });
};
