import { ErrorMessage, FastField } from "formik";
import React, { useState } from "react";

export const Select_Input = ({
  label,
  option,
  firstItem,
  name,
  parent_title,
  showSubInput,
  data,
  stateOfData,
  setForceRender,
}) => {
  //const [data, setData] = useState([]);
  return (
    <div className="p-2">
      <div className="input-group mb-1">
        <span className="input-group-text w_6rem customStyle">{label}</span>
        <FastField
          as="select"
          className="form-control form-select"
          id={name}
          name={name}
          value={data}
          onChange={(e) => {
            e.preventDefault();
            if (showSubInput) {
              console.log(e.target);
              setData(e.target.value);
              stateOfData(e.target.value);
              setForceRender((i) => i + 1);
            }
          }}
        >
          {parent_title ? (
            <option>{parent_title}</option>
          ) : (
            <>
              {/* <option> ÿØÿ≥ÿ™Ÿá ŸàÿßŸÑÿØ ÿ±ÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ... </option>  */}
              <option hidden value className="text-danger">
                {firstItem ? firstItem : "--"}
              </option>
              {option.map((d) => (
                <option className="text-dark" key={Math.random()} value={d.id}>
                  {d.value}
                </option>
              ))}
            </>
          )}
        </FastField>
      </div>
      <ErrorMessage
        name={name}
        render={(msg) => <small className="text-danger">{msg}</small>}
      />
    </div>
  );
};

