import React from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_roles";
import { SubmitBtn } from "../../formik/SubmitBtn";
export const AddNewRole_formik = ({
  reInitialize,
  rowDataToEdit,
  permissions,
}) => {
  return (
    <div>
      <Formik
        initialValues={reInitialize || initialValues}
        onSubmit={(values, action) => onSubmit(values, action, reInitialize)}
        validationSchema={validationSchema}
        enableReinitialize
      >
        {(form) => {
          console.log(form.values);

          return (
            <Form id="formOfRoles">
              <FormikControl
                control="input"
                name="title"
                title="عنوان نقش"
                placeholder="فقط حروف فارسی و لاتین"
              />
              <FormikControl
                control="textArea"
                name="description"
                title="توضیحات نقش"
                placeholder="فقط حروف فارسی و لاتین"
              />
              {reInitialize ? (
                <div className="row">
                  <span>دسترسی ها : </span>
                  <span className="text-secondary">
                    {rowDataToEdit?.permissions.map((p) => (
                      <span className="m-2">{`${p.description}`} ،</span>
                    ))}
                  </span>
                </div>
              ) : (
                <FormikControl
                  control="checkbox"
                  name="permissions_id"
                  label="دسترسی ها:"
                  options={permissions}
                />
              )}
              <div className="d-flex justify-content-center">
                <SubmitBtn id={reInitialize?.id} />
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};
