import * as Yup from "yup";
import { createNewProductService } from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  category_ids: "",
  title: "",
  price: "",
  weight: "",
  brand_id: "",
  color_ids: "",
  guarantee_ids: "",
  descriptions: "",
  short_descriptions: "",
  cart_descriptions: "",
  image: "",
  alt_image: "",
  discount: "",
  keywords: "",
  stock: "",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (rowData) => {
  log
  const res = await createNewProductService(rowData);
  console.log(res.data.data);
  if (res.status == 201) {
    swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({
  category_ids: Yup.string().required("cc"),
});
