import * as Yup from "yup";
import {
  createNewAttributeService,
  updateAttributeService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

// ==============  üìçheader of table in add attributes ===================
export const headers_attributesTable = [
  { field: "id", title: "id" },
  { field: "title", title: "ÿπŸÜŸàÿßŸÜ ŸÖÿ≠ÿµŸàŸÑ" },
  { field: "unit", title: "Ÿàÿßÿ≠ÿØ" },
  { field: "in_filter", title: "ŸÜŸÖÿß€åÿ¥ ÿØÿ± ŸÅ€åŸÑÿ™ÿ±" },
];

// ==============  üìçinitialValues add attributes ===================
export const initialValues = {
  attributeTitle: "",
  attributeUnit: "",
  switchShowFilter: false,
};

//================  üìçonSubmit add attributes ======================
export const onSubmit = async (
  values,
  actions,
  location,
  setAttData,
  singleAttId,
  setReInitialValues
) => {
  if (singleAttId.length != 0) {
    console.log("singleAttId");
    values = {
      ...values,
      switchShowFilter: values.switchShowFilter ? 1 : 0,
    };
    const res = await updateAttributeService(singleAttId, values);
    try {
      console.log(res);
      if (res.status == 200) {
        swal("", res.data.message, "success");
        setReInitialValues({
         //...reInitialValues, //‚≠ê
          attributeTitle: res.data.data.title,
          attributeUnit: res.data.data.unit,
          switchShowFilter: res.data.data.in_filter ? true : false,
        });
        actions.resetForm();
      }
    } catch {}
  } else if(singleAttId.length == 0) {
    console.log(singleAttId);
    values = {
      ...values,
      switchShowFilter: values.switchShowFilter ? 1 : 0,
    };
    const res = await createNewAttributeService(
      location.state?.categoryId,
      values
    );
    try {
      console.log(res);
      if (res.status == 201) {
        swal("", res.data.message, "success");
        setAttData((oldData) => [...oldData, res.data.data]); //ÿß€åŸÜ ÿÆÿ∑ ⁄Ü€å⁄©ÿßÿ± ŸÖ€å⁄©ŸÜŸáÿü ÿ¨ÿØŸàŸÑ Ÿà€å⁄ò⁄Ø€å Ÿáÿßÿ±Ÿà ÿ±ŸÜÿØÿ± ŸÖÿ¨ÿØÿØ ŸÖ€å⁄©ŸÜŸá üòÅ
        actions.resetForm();
      }
    } catch {}
  }
};

//===============  üìçvalidationSchema add attributes ===============
export const validationSchema = Yup.object({
  attributeTitle: Yup.string().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
  attributeUnit: Yup.string().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
});
