import * as Yup from "yup";
import {
  createNewBrandService,
  updateBrandService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";
import { useEffect } from "react";

//====================== 📍initialValues =====================
export const initialValues = {
  original_name: "",
  persian_name: "",
  descriptions: "",
  logo: null,
};

//====================== 📍onSubmit ===========================
export const onSubmit = async (
  data,
  actions,
  tableData,
  setTableData,
  brandToEdit,
  brandId
) => {
  if (brandToEdit) {
    // console.log(brandToEdit);
    const res = await updateBrandService(brandId, brandToEdit);
    // console.log(res);
    if (res.status == 200) {
      swal("اعمال شد", res.data.message, "success");
      setTableData([...tableData, res.data.data]);
      actions.resetForm();
    } else {
      actions.resetForm();
    }
  } else {
    const res = await createNewBrandService(data);
    if (res.status == 201) {
      swal("ثبت شد", res.data.message, "success");
      setTableData([...tableData, res.data.data]);
      actions.resetForm();
    } else {
      actions.resetForm();
    }
  }
};

//====================== 📍validationSchema ===================
export const validationSchema = Yup.object({
  original_name: Yup.string()
    .required("لطفا این قسمت را پر کنید")
    .matches(/^[0-9A-Za-z]{1,}$/, "فقط حروف انگلیسی"),
  persian_name: Yup.string().matches(/^[\u0600-\u06FF\s]+$/, "فقط حروف فارسی"),

  descriptions: Yup.string().matches(
    /^[0-9A-Za-z\u0600-\u06FF\s]+$/,
    "فقط حروف و اعداد مجازند"
  ),

  logo: Yup.mixed()
    .test("filesize", "حجم فایل نمیتواند بیشتر 500 کیلوبایت باشد", (value) =>
      !value ? true : value.size <= 500 * 1024
    )
    .test("format", "فرمت فایل باید png یا jpg باشد", (value) =>
      !value ? true : value.type === "image/jpeg" || "image/png"
    ),
});
