import * as Yup from "yup";
import {
  createNewGuaranteeService,
  updateGuaranteeService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  descriptions: "",
  length: "",
  length_unit: "",
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  data,
  setData,
  guaranteeToEdit
) => {
  if (guaranteeToEdit) {
    const res = await updateGuaranteeService(guaranteeToEdit, guaranteeToEdit.id);
    console.log(res);
  } else {
    const res = await createNewGuaranteeService(values);
    if (res.status == 201) {
      setData((data) => [...data, res.data.data]);
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");
      actions.resetForm();
    }
  }
};
//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
