import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  validationSchema,
} from "../../5-colour/FormikHelper_colour";
import { onSubmit } from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";

export const AddProduct = ({ title_of_page }) => {
  const [mainCategories, setMainCategories] = useState([]);

handleGet



  return (
    <div className="">
      {/* ================= 📍 header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h3>title_of_page</h3>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== 📍 start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={initialValues}
          onSubmit={onSubmit}
          validationSchema={validationSchema}
        >
          {(form) => {
            return (
              <Form>
                {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label=""
                    option={mainCategories}
                    name=""
                    parent_title=""
                  />
                ) : (
                  "دسته والد"
                )}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  placeholder="قیمت"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  placeholder="وزن"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="color_ids"
                  placeholder="رنگ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="guarantee_ids"
                  placeholder="گارانتی"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" تگ ها"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  placeholder="موجودی"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  placeholder="تخفیف"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
