import React from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { getAttributesService } from "../../../../services/CRUD_categoryService";
import { useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_AddAtt_products";
import { SubmitBtn } from "../../../formik/SubmitBtn";

export const AddAttributes = () => {
  const location = useLocation();
  const [typeAtt, setTypeAtt] = useState([]);
  const [titleAttrName, setTitleAttrName] = useState([]);

  useEffect(() => {
    console.log(location.state.rowData.categories.map((item) => item.title));
    setTitleAttrName(
      location.state.rowData.categories.map((item) => item.title)
    );
  }, [location]);

  //============= گرفتن تایپ ویژگی ها از محصول والد =============
  const handleGetTypeOfAtt = async () => {
    const res = await getAttributesService(
      location.state.rowData.categories[0].id
    );
    if (res.status == 200) {
      setTypeAtt(res.data.data);
    }
  };
  useEffect(() => {
    handleGetTypeOfAtt();
  }, []);

  return (
    <div className=" container mt-5">
      {/* ================= 📍 👇header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container ">
        <span className="d-flex">
          <h5 className="fw-bold">افزودن ویژگی جدید :</h5>
          <h5 className="text-primary px-2">{location.state.rowData.title}</h5>
        </span>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>
      {/* ================= 📍 👆header of page  ==================== */}
      <div className="d-flex justify-content-center">
        <div className="col-12 col-xxl-7 col-xl-8 col-lg-10">
          <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
          >
            {(formik) => {
              // console.log(formik.values);

              return (
                <div className="">
                  {typeAtt.length ? (
                    <Form>
                      {titleAttrName.map((title) => {
                        return (
                          <div>
                            <div className="text-center py-2">
                              <span className="fs-4"> گروه: </span>
                              <span className="fs-5">{title}</span>
                            </div>
                            <FormikControl
                              control="typeOfAtt"
                              name="typeOfAtt"
                              titleOfAttr=""
                              unitOfAttr=""
                              typeAtt={typeAtt}
                              rowData={location.state.rowData}
                            />
                          </div>
                        );
                      })}

                      <div className=" text-center pt-4">
                        <SubmitBtn />
                      </div>
                    </Form>
                  ) : (
                    <div className="text-primary fs-4 fw-bold p-5 text-center">
                      هیچ ویژگی برای این محصول یافت نشد...
                    </div>
                  )}
                </div>
              );
            }}
          </Formik>
        </div>
      </div>
    </div>
  );
};
