import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import {
  useLocation,

} from "react-router-dom";
import {
  handleGetBrands,
  handleGetColors,
  handleGetGuarantees,
  handleGetMainCategories,
  handleGetParentCategories,
} from "./Get_Items_dropdowns";

export const AddProduct = () => {
  // ====================== useStates üëá ===========================
  const [parentsCategories, setParentsCategories] = useState([]); // ŸÜ⁄ØŸáÿØÿßÿ±€å ŸáŸÖŸá ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸàÿßŸÑÿØ - ÿßŸàŸÑ€åŸÜ ŸÖÿ≠ÿµŸàŸÑÿßÿ™€å ⁄©Ÿá ŸàÿßŸÑÿØ€å ŸÜÿØÿßÿ±ŸÜÿØ
  const [mainCategories, setMainCategories] = useState([]); // ŸÜ⁄ØŸáÿØÿßÿ±€å ŸáŸÖŸá ÿØÿ≥ÿ™Ÿá Ÿáÿß€å ÿßÿµŸÑ€å
  const [colors, setColors] = useState([]);
  const [brands, setBrands] = useState([]);
  const [guarantee, setGuarantee] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const [idOfParent, setIdOfParent] = useState("");
  const [chips, setChips] = useState([]);
  const [chips_color, setChips_color] = useState([]);
  const [chips_guarantee, setChips_guarantee] = useState([]);
  const [selectedCat, setSelectedCat] = useState([]);
  const [selectedColor, setSelectedColor] = useState([]);
  const [selectedGuarantee, setSelectedGuarantee] = useState([]);
  // ====================== useStates üëÜ ===========================

  const location = useLocation();
 
  useEffect(() => {
    if (location.state) {
      setReInitialize({
        category_ids: location.state.rowData?.categories
          .map((item) => item.id)
          .join(" - "),
        color_ids: location.state.rowData?.colors
          ?.map((item) => item.id)
          .join(" - "),
        guarantee_ids: location.state.rowData?.guarantees
          ?.map((item) => item.id)
          .join(" - "),
        ...location.state.rowData,
      });
    }
  }, [location]);
  //========== üìçÿ®ÿ±ÿß€å ÿ¨ÿß€å⁄Øÿ∞ÿßÿ±€å ÿØÿ± ŸÅÿ±ŸÖ Ÿà€åÿ±ÿß€åÿ¥ ŸÖÿ≠ÿµŸàŸÑ ===========
  const handleSelectedInitialValue = () => {
    setSelectedCat(
      location.state?.rowData?.categories.map((c) => {
        return { id: c.id, value: c.title };
      })
    );
    setSelectedColor(
      location.state?.rowData?.colors.map((c) => {
        return { id: c.id, value: c.title };
      })
    );
    setSelectedGuarantee(
      location.state?.rowData?.guarantees.map((c) => {
        return { id: c.id, value: c.title };
      })
    );
  };
  //===========================================================
  useEffect(() => {
    handleGetParentCategories(setParentsCategories); //ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸàÿßŸÑÿØ-⁄©Ÿá ŸàÿßŸÑÿØ€å ŸÜÿØÿßÿ±ŸÜÿØ- ÿ±Ÿà ŸÖ€å⁄Ø€åÿ±Ÿá
    handleGetColors(setColors); //ÿ±ŸÜ⁄Ø Ÿáÿß ÿ±Ÿà ŸÖ€å⁄Ø€åÿ±Ÿá
    handleGetBrands(setBrands); // ÿ®ÿ±ŸÜÿØŸáÿß ÿ±Ÿà ŸÖ€å⁄Ø€åÿ±Ÿá
    handleGetGuarantees(setGuarantee); //⁄Øÿßÿ±ÿßŸÜÿ™€å Ÿáÿß ÿ±Ÿà ŸÖ€å⁄Ø€åÿ±Ÿá
    handleSelectedInitialValue();
  }, []);

  // ==================== üìç⁄Øÿ±ŸÅÿ™ŸÜ ŸàÿßŸÑÿØŸáÿß ======================
  useEffect(() => {
    setMainCategories(null);
    handleGetMainCategories(idOfParent, setMainCategories);
  }, [idOfParent, parentsCategories]);

  return (
    <div>
      {/* ================= üìç header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>
          {location.state.title_for_adding
            ? location.state.title_for_adding
            : location.state.title_for_editing + `" ${reInitialize.title} "`}
        </h5>
        {/*üëá icon ‚ùå  */}
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== üìç start form  ======================= */}
      <div className="container col-lg-8 col-xl-7 col-xxl-5 ">
        <Formik
          initialValues={reInitialize || initialValues}
          onSubmit={(value, action) =>
            onSubmit(
              value,
              action,
              reInitialize,
              setReInitialize,
              setChips_color,
              setChips_guarantee
            )
          }
          validationSchema={validationSchema}
          enableReinitialize
        >
          {(form) => {
            setIdOfParent(form.values.parent_id);

            return (
              <Form>
                <div>
                  <FormikControl
                    control="select"
                    label="ÿØÿ≥ÿ™Ÿá ŸàÿßŸÑÿØ"
                    option={parentsCategories}
                    name="parent_id"
                    stateOfData={setIdOfParent}
                    addBtnOption={true}
                    addBtnPath="/product-group-management"
                  />
                </div>
                {selectedCat || (idOfParent && mainCategories?.length > 0) ? (
                  <FormikControl
                    control="multiSelect"
                    label="ÿØÿ≥ÿ™Ÿá ÿßÿµŸÑ€å"
                    option={mainCategories}
                    name="category_ids"
                    chips={chips}
                    setChips={setChips}
                    dynamicPath={true}
                    idOfParent={idOfParent}
                    addBtnOption={true}
                    addBtnPath="/product-group-management"
                    selectedItems={selectedCat}
                  />
                ) : null}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="ÿπŸÜŸàÿßŸÜ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  title="ŸÇ€åŸÖÿ™"
                  placeholder="ŸÇ€åŸÖÿ™ (ÿ™ŸàŸÖÿßŸÜ)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  title="Ÿàÿ≤ŸÜ"
                  placeholder="Ÿàÿ≤ŸÜ (⁄©€åŸÑŸà⁄Øÿ±ŸÖ)"
                />
                <FormikControl
                  control="select"
                  label="ÿ®ÿ±ŸÜÿØ"
                  option={brands}
                  name="brand_id"
                  addBtnOption={true}
                  addBtnPath="/brand-management"
                />
                <FormikControl
                  control="multiSelect"
                  label="ÿ±ŸÜ⁄Ø"
                  option={colors}
                  name="color_ids"
                  chips={chips_color}
                  setChips={setChips_color}
                  addBtnOption={true}
                  addBtnPath="/colour-management"
                  selectedItems={selectedColor}
                />

                <FormikControl
                  control="multiSelect"
                  label="⁄Øÿßÿ±ÿßŸÜÿ™€å"
                  option={guarantee}
                  name="guarantee_ids"
                  chips={chips_guarantee}
                  setChips={setChips_guarantee}
                  addBtnOption={true}
                  addBtnPath="/guarantee-management"
                  selectedItems={selectedGuarantee}
                />
                <FormikControl
                  control="ckEditor"
                  type="type"
                  name="descriptions"
                  placeholder="ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™"
                />
                <FormikControl
                  control="addFile"
                  formik={form}
                  label="ÿ™ÿµŸà€åÿ±"
                  name="image"
                  placeholder="üìÇ ÿ®ÿ±ÿß€å ÿßŸÜÿ™ÿÆÿßÿ® ÿ™ÿµŸà€åÿ± ÿß€åŸÜÿ¨ÿß ⁄©ŸÑ€å⁄© ⁄©ŸÜ€åÿØ"
                />
                <FormikControl
                  control="input"
