import React, { useEffect } from "react";

export const Header = () => {
  useEffect(() => {
    document
      .getElementById("flexSwitchChecked")
      .addEventListener("change", function () {
        if (this.checked) {
          document.querySelector(".mini_sidebar").classList.add("expanded");
        }else{
          document.querySelector(".mini_sidebar")
        }
      });
  }, []);

  return (
    <div className="headerDiv w-100">
      <div className="header w-100 d-flex justify-content-between">
        <div className="form-check form-switch d-flex align-items-center justify-content-center w-25">
          <input
            type="checkbox"
            className="form-check-input pointer"
            id="flexSwitchChecked"
          />
        </div>
        <div className="d-flex justify-content-end align-items-center">
          <i className="fa-solid fa-magnifying-glass p-3 fs-4 headerLogos pointer"></i>
          <i className="fa-regular fa-bell p-3 fs-4 headerLogos pointer"></i>
          <i className="fa-sharp fa-solid fa-list-ul p-3 fs-4 headerLogos pointer"></i>
        </div>
      </div>
    </div>
  );
};
