import React from "react";
import * as Yup from "yup";
import { FormikControl } from "./FormikControl_AddItems";

export const FormikAddItems = () => {
  const initialValues = {
    title: "title",
  };
  return (
    <Formik initialValues={initialValues} onSubmit={() => console.log("test")}>
      {(formik) => {
        console.log(formik);
        return (
     //   <div className="bg-success vh-100 w-100 d-flex justify-content-center align-items-center">

            <Form className="">
            <FormikControl control="select" />
              <FormikControl
                formik={formik}
                control="input"
                type="text"
                name="عنوان"
                placeholder="عنوان"
                inputStyle="registerInputStyle"
              />
              <FormikControl control="textArea" />
              <FormikControl control="addFile" />
              <FormikControl control="checkbox" />
            </Form>
     
         
        );
      }}
    </Formik>
  );
};
