import React, { useEffect, useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_brands";

export const AddNewBrands = ({
  data,
  setData,
  brandToEdit,
  setBrandToEdit,
}) => {
  const brandId = brandToEdit?.id;
  // useEffect(() => {}, []);

  return (
    <div>
      <button
        type="button"
        className="btn btn-success"
        data-bs-toggle="modal"
        data-bs-target="#add-brand-modal"
      >
        افزودن برند جدید
      </button>

      {/* ================= 📍start modal ================= */}
      <div
        className="modal fade modal-dialog modal-dialog-centered"
        id="add-brand-modal"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            {/* ==============  modal-body ============== */}
            <div className="modal-body ">
              <Formik
                initialValues={brandToEdit || initialValues}
                onSubmit={(values, actions) => {
                  onSubmit(values, actions, data, setData);
                }}
                validationSchema={validationSchema}
                enableReinitialize
              >
                {(formik) => {
                  return (
                    <Form>
                      {/* ==============  modal-header ============= */}
                      <div className="modal-header">
                        <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                          {brandToEdit
                            ? `ویرایش برند ${brandToEdit.original_name}`
                            : "افزودن برند جدید"}
                        </h1>
                        {/* =========== ❌ (exit the modal) ========== */}
                        <button
                          type="button"
                          className="btn-close "
                          data-bs-dismiss="modal"
                          aria-label="Close"
                          onClick={() => formik.resetForm()}
                        ></button>
                      </div>
                      {/* ==============  modal-body ============== */}
                      <div className="pt-3">
                        <FormikControl
                          control="input"
                          type="text"
                          name="original_name"
                          placeholder="عنوان لاتین"
                        />

                        <FormikControl
                          control="input"
                          type="text"
                          name="persian_name"
                          placeholder="عنوان فارسی"
                        />

                        <FormikControl
                          control="textArea"
                          type="text"
                          name="descriptions"
                          placeholder="توضیحات"
                        />
                        {brandToEdit ? <div>

                        <img
              src={apiPath + rowData.logo}
              alt={`${rowData.original_name} logo`}
              width="40"
            /> 
                        </div> : null}
                        <FormikControl
                          formik={formik}
                          control="addFile"
                          type="text"
                          label="تصویر"
                          name="logo"
                          placeholder="برای افزودن تصویر اینجا کلیک کنید"
                        />
                      </div>
                      <div className="text-center">
                        <SubmitBtn brandId={brandId} />
                      </div>
                    </Form>
                  );
                }}
              </Formik>
            </div>
          </div>
        </div>
      </div>
      {/* ================= 📍end modal ================= */}
    </div>
  );
};
