import * as Yup from "yup";
import {
  createNewColorService,
  updateColorService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  category_ids: "",
  color_ids: "",
  discount: "",
  guarantee_ids: "",
  keywords: "",
  price: "",
  stock: "",
  title: "",
  weight: "",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  colorToEdit,
  setData,
  setColorToEdit,
  setColorPickerValue
) => {
  //===== ‚≠ê Ÿà€åÿ±ÿß€åÿ¥ €å⁄©€å ÿßÿ≤ ÿ±ŸÜ⁄Ø Ÿáÿß =====================
  if (colorToEdit) {
    const res = await updateColorService(values, colorToEdit.id);
   console.log(res.data);
    if (res.status == 200) {
      swal("Ÿà€åÿ±ÿß€åÿ¥ ÿ¥ÿØ!...", res.data.message, "success");
      setColorToEdit(res.data.data);
      setData((oldData) => {
        let newData = [...oldData];
        let index = newData.findIndex((d) => d.id == colorToEdit.id);
        newData[index] = res.data.data;
        return newData;
      });
    }
    // ======= ‚≠ê ÿßŸÅÿ≤ŸàÿØŸÜ €å⁄© ÿ±ŸÜ⁄Ø ÿ¨ÿØ€åÿØ ===================
  } else {
    const res = await createNewColorService(values);
    if (res.status == 201) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
      setData((oldData) => [...oldData, res.data.data]);
      setColorPickerValue("#000");
      actions.resetForm();
    }
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
