import React from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { getAttributesService } from "../../../../services/CRUD_categoryService";
import { useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";

export const AddAttributes = () => {
  const location = useLocation();
  const [typeAtt, setTypeAtt] = useState([]);

  // useEffect(() => {
  //   console.log(location.state.rowData);
  //   console.log(location.state.rowData.categories[0].id);
  // }, [location]);
  //============= ⁄Øÿ±ŸÅÿ™ŸÜ ÿ™ÿß€åŸæ Ÿà€å⁄ò⁄Ø€å Ÿáÿß ÿßÿ≤ ŸÖÿ≠ÿµŸàŸÑ ŸàÿßŸÑÿØ =============
  const handleGetTypeOfAtt = async () => {
    const res = await getAttributesService(
      location.state.rowData.categories[0].id
    );
    if (res.status == 200) {
      setTypeAtt(res.data.data);
      // console.log(res.data.data);
    }
  };
  useEffect(() => {
    handleGetTypeOfAtt();
  }, []);

  return (
    <div className=" container mt-5">
      {/* ================= üìç üëáheader of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container ">
        <h5 className="fw-bold">ÿßŸÅÿ≤ŸàÿØŸÜ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ :</h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>
        {/* ================= üìç header of page  ==================== */}
      <div className="d-flex justify-content-center">
        <div className="col-12 col-xxl-7 col-xl-8 col-lg-10">
          <Formik>
            {(formik) => {
              return (
                <div className="">
                  <Form>
                    <FormikControl control="typeOfAtt" name="typeOfAtt" />
                  </Form>
                </div>
              );
            }}
          </Formik>
        </div>
      </div>
    </div>
  );
};
