import React, { useState } from "react";
import { AddItem_btn } from "../../general_compo/add_or_edit_product/AddItem_btn";
import { Field, Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_discounts";

export const AddNewDiscounts_modal = () => {
  const [dateOfExpire, setDataOfExpire] = useState([]);


  jalaliDatepicker.startWatch();


  return (
    <div>
      {/* <!-- Button trigger modal --> */}
      <span data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        <AddItem_btn />
      </span>

      {/* <!-- Modal --> */}
      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header d-flex justify-content-between align-items-center">
              <h5 className="modal-title w-100 mx-5" id="staticBackdropLabel">
                افزودن کد تخفیف
              </h5>
              <button
                type="button"
                className="btn-close w-25 me-5"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>

            <div className="modal-body">
              <Formik
                initialValues={initialValues}
                onSubmit={(values) => onSubmit(values)}
                validationSchema={validationSchema}
              >
                {(form) => {
                  return (
                    <Form>
                      <FormikControl
                        control="input"
                        type="text"
                        name="title"
                        placeholder="عنوان"
                      />
                      <FormikControl
                        control="input"
                        type="text"
                        name="code"
                        placeholder="کد تخفیف"
                      />
                      <FormikControl
                        control="input"
                        type="text"
                        name="percent"
                        placeholder="درصد تخفیف"
                      />
                      {/* <FormikControl
                        control="input"
                        type="text"
                        name="expire_at"
                        placeholder="تاریخ انقضا"
                      /> */}

                      {/* <input
                        type="date"
                        id="start"
                        name="trip-start"
                        value="2018-07-22"
                        min="2010-01-01"
                        max="2018-12-31"
                      /> */}

                      <Field
                        name="expire_at"
                        type="date"
                        value={dateOfExpire}
                        // min="2018-01-01"
                        // max="2018-12-31"
                        onChange={(e) => {
                          setDataOfExpire(e.target.value);
                        }}
                      />
                      <input data-jdp />
                      <div className="text-center">
                        <SubmitBtn />
                      </div>
                    </Form>
                  );
                }}
              </Formik>
            </div>
            {/* <div className="modal-footer">
              <button
                type="button"
                className="btn btn-success
              "
              >
                ذخیره
              </button>
            </div> */}
          </div>
        </div>
      </div>
    </div>
  );
};
