import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { FastField, Form, Formik } from "formik";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  handleGetBrands,
  handleGetColors,
  handleGetGuarantees,
  handleGetMainCategories,
  handleGetParentCategories,
} from "./Get_Items_dropdowns";
import { getCategoriesService } from "../../../../services/CRUD_categoryService";

export const AddProduct = () => {
  const [parentsCategories, setParentsCategories] = useState([]); // نگهداری همه محصولات والد - اولین محصولاتی که والدی ندارند
  const [mainCategories, setMainCategories] = useState([]); // نگهداری همه دسته های اصلی
  const [colors, setColors] = useState([]);
  const [brands, setBrands] = useState([]);
  const [guarantee, setGuarantee] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const [idOfParent, setIdOfParent] = useState("");
  const [forceRender, setForceRender] = useState(1);

  const location = useLocation();

  useEffect(() => {
    setReInitialize({
      category_ids: location.state.rowData?.categories[0].id,
      color_ids: location.state.rowData?.colors[0].id,
      ...location.state.rowData,
    });
  }, [location]);

  useEffect(() => {
    handleGetParentCategories(setParentsCategories); //محصولات والد-که والدی ندارند- رو میگیره
    handleGetColors(setColors); //رنگ ها رو میگیره
    handleGetBrands(setBrands); // برندها رو میگیره
    handleGetGuarantees(setGuarantee);
  }, []);
  useEffect(() => {
    handleGetMainCategories(idOfParent, setMainCategories);
    setForceRender(1);
  }, [idOfParent, parentsCategories, forceRender]);

  const handleSetMainCategories = async (value) => {
    setMainCategories("waiting");
    if (value > 0) {
      const res = await getCategoriesService(value);
      if (res.status === 200) {
        setMainCategories(
          res.data.data.map((d) => {
            return { id: d.id, value: d.title };
          })
        );
      }
    } else {
      setMainCategories([]);
    }
  };

  return (
    <div>
      {/* ================= 📍 header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>
          {location.state.title_for_adding
            ? location.state.title_for_adding
            : location.state.title_for_editing + `" ${reInitialize.title} "`}
        </h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== 📍 start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={reInitialize || initialValues}
          onSubmit={(value, action) =>
            onSubmit(value, action, reInitialize, setReInitialize)
          }
          validationSchema={validationSchema}
          enableReinitialize
        >
          {(form) => {
            console.log(form.values);
            return (
              <Form>
                {parentsCategories.length ? (
                  <FormikControl
                    control="select"
                    label="دسته والد"
                    option={parentsCategories}
                    name="parent_id"
                    handleOnchange={handleSetMainCategories}
                  />
                ) : (
                  <div className="d-flex align-items-center pe-3">
                    <div className="text-dark ps-2">دسته والد</div>{" "}
                    <div
                      class="spinner-border spinner-border-sm text-secondary"
                      role="status"
                    ></div>
                  </div>
                )}

                {/* <div className="p-2">
                  <div className="input-group mb-1">
                    <span className="input-group-text w_6rem customStyle">
                      دسته اصلی
                    </span>
                    <FastField
                      as="select"
                      className="form-control form-select"
                      id="parent_id"
                      name="category_ids"
                      handleOnchange={handleSetMainCategories}
                    >
                      <option>--</option>
                      {mainCategories.map((d) => (
                        <option value={d.id}>{d.value}</option>
                      ))}
                    </FastField>
                  </div>
                </div> */}

                <FormikControl
                  control=""
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  title="قیمت"
                  placeholder="قیمت (تومان)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  title="وزن"
                  placeholder="وزن (کیلوگرم)"
                />
                {brands.length && (
                  <FormikControl
                    control="select"
                    label="برند"
                    option={brands}
                    name="brand_id"
                  />
                )}
                {colors.length && (
                  <FormikControl
                    control="select"
                    label="رنگ"
                    option={colors}
                    name="color_ids"
                  />
                )}
                {guarantee.length && (
                  <FormikControl
                    control="select"
                    label="گارانتی"
                    option={guarantee}
                    name="guarantee_ids"
                  />
                )}
                <FormikControl
                  control="textArea"
                  type="type"
                  name="descriptions"
                  placeholder="توضیحات"
                />
                <FormikControl
                  control="addFile"
                  formik={form}
                  label="تصویر"
                  name="image"
                  placeholder="📂 برای انتخاب تصویر اینجا کلیک کنید"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="alt_image"
                  placeholder="توضیح تصویر"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" تگ ها"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  title="موجود"
                  placeholder="موجودی (تعداد)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  title="تخفیف"
                  placeholder="درصد تخفیف"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn id={reInitialize.id} />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
