import { FastField } from "formik";
import React from "react";
import { Spinner } from "react-bootstrap";

export const SubmitBtn = ({ categoryId }) => {
  return (
    <FastField as>
      {({ form }) => {
        return (
          <button className="btn  pt-4" disabled={form.isSubmitting}>
            <span>
              {categoryId ? (
                <button type="submit" className="btn btn-warning">
                  ذخیره تغییرات
                  {form.isSubmitting ? (
                    <Spinner
                      className="me-3 text-dark"
                      animation="border"
                      variant="light"
                      size="sm"
                    />
                  ) : null}
                </button>
              ) : (
                <button type="submit" className="btn btn-success">
                  ذخیره
                  {form.isSubmitting ? (
                    <Spinner
                      className="me-3 text-dark"
                      animation="border"
                      variant="light"
                      size="sm"
                    />
                  ) : null}
                </button>
              )}
            </span>
          </button>
        );
      }}
    </FastField>
  );
};
